ValidationRule.errorConditionFormula:
AND( $Profile.Name != 'Data Loader Profile', RecordType.Name == 'Indirect Reseller GPP 3.0', Text(Accreditation_Type__c) == 'Product',
OR( NOT(OR( Text(Accreditation_Level__c) = 'Authorized', Text(Accreditation_Level__c) = 'Advanced', Text(Accreditation_Level__c) = 'Expert' ) ), NOT(OR( Text( Pricing_Level_Override__c) = 'Authorized', Text(Pricing_Level_Override__c) = 'Advanced', Text(Pricing_Level_Override__c) = 'Expert', Text(Pricing_Level_Override__c) = 'Custom', Text(Pricing_Level_Override__c) = '' ) ) )
)
