CustomField.formula:
IF((ISPICKVAL(Status,"Closed") || ISPICKVAL(Status,"Completed") || ISPICKVAL(Origin, "Portal"))
	,IF	( 	OR	(CH_Invitation_Link__c="Completed"
				, CH_Invitation_Link__c="Expired"
				)
				, CH_Invitation_Link__c
				, IF	( OR( ISBLANK(CH_Invitation_Link__c), ISPICKVAL(CH_ServiceType__c,"Internal Support"))
						, "Not Applicable"
						, HYPERLINK(CH_Invitation_Link__c , "Pending")
						)
		)
	,IF	(	AND(OR	( ISPICKVAL(Entitlement.ServiceContract.CH_SWS_Survey__c, "Enabled")
				, AND	( ISPICKVAL(Entitlement.ServiceContract.CH_SWS_Survey__c, "Use Legal Entity Setting")
						, ISPICKVAL(Account.CH_SWS_Survey__c, "Enabled")
						)

				),NOT(ISPICKVAL(CH_ServiceType__c,"Internal Support")))
		, "Applicable"
		, "Not Applicable"
	)
)
