ValidationRule.errorConditionFormula:
AND (
	$Profile.Name<>$Label.Data_Loader_Profile_Name, 
	NOT(ISBLANK(Email)), 
	OR(
		AND(ISPICKVAL(CH_ContactType__c, 'Nokia Employee'), 
			NOT(CONTAINS(LOWER($Setup.Nokia_Employee_Email_Domains__c.Nokia_Valid_Domain__c), 
				'"' & LOWER(TRIM(RIGHT(Email, LEN(Email) - FIND("@", Email)))) & '"'
			))
		),
		AND(NOT(ISPICKVAL(CH_ContactType__c, 'Nokia Employee')), 
			CONTAINS(LOWER($Setup.Nokia_Employee_Email_Domains__c.Nokia_Invalid_Domain__c), 
				'"' & LOWER(TRIM(MID(Email, FIND("@", Email) + 1, FIND(".", RIGHT(Email, LEN(Email) - FIND("@", Email))) - 1))) & '"'
			)
		)
	)
)
