ValidationRule.errorConditionFormula:
AND( 
    Event__r.RecordType.DeveloperName = "ERM" 
    , OR( 
        OR(ISPICKVAL(Role__c, "Content") 
    ,      ISPICKVAL(Role__c, "CT Member")
										)
								)
    , OR( 
        ISPICKVAL(Status__c, "Confirmed") 
        , ISPICKVAL(Status__c, "Rejected") 
        , ISPICKVAL(Status__c, "Partial Confirmed") 
    ) 
)
