ValidationRule.errorConditionFormula:
IF (
!ISNEW(),
IF( ISCHANGED(HWS_Suspension_Status__c),
IF( AND( TEXT(PRIORVALUE( HWS_Suspension_Status__c))='Pending Review' , AND(TEXT(HWS_Suspension_Status__c) != 'Suspended', TEXT(HWS_Suspension_Status__c)!='Rejected' ) ), TRUE, IF( AND( TEXT(PRIORVALUE( HWS_Suspension_Status__c))='Suspended' , TEXT(HWS_Suspension_Status__c) != 'UnSuspended' ), TRUE, IF( AND( TEXT(PRIORVALUE( HWS_Suspension_Status__c))='UnSuspended' , TEXT(HWS_Suspension_Status__c) != 'Pending Review' ), TRUE, IF( AND( TEXT(PRIORVALUE( HWS_Suspension_Status__c))='Rejected' , TEXT(HWS_Suspension_Status__c) != 'Pending Review' ), TRUE, FALSE
)
)
)
)
,
FALSE
)
,
FALSE
)
