ValidationRule.errorConditionFormula:
AND( OR( TEXT(Status ) = 'Working - Contacted', TEXT(Status ) = 'Sent to Partner' ), OR( RecordType.Name = "Distributor Deal Registration", RecordType.Name = "Indirect Deal Registration", RecordType.Name = "Reseller Deal Registration"), (NOT(OR(
($Profile.Name = 'PRM Partner Community User Login'), ($Profile.Name = 'PRM Partner Relationship Manager Login'), (CONTAINS($UserRole.Name ,'Partner Sales Manager'))
) )) , NOT($Profile.Name = 'Data Loader Profile') )
