ValidationRule.errorConditionFormula:
AND( 
OR( 
RecordType.DeveloperName == 'Direct_Record_Type', 
RecordType.DeveloperName == 'Indirect_Record_Type' 
), 
ISCHANGED(Probability), 
OR( 
AND(OR( 
ISPICKVAL(StageName ,'Handover (Prepare for Delivery)'), 
ISPICKVAL(StageName ,'Execute (Start Delivery)') 
), 
!ISPICKVAL( Apttus_Approval__Approval_Status__c , 'Approved') 
), 
AND( 
ISPICKVAL(PRIORVALUE(StageName) ,'Execute (Start Delivery)'), 
ISPICKVAL((StageName) ,'Execute (Start Delivery)')	
) 
), 
$Profile.Name <> $Label.Data_Loader_Profile_Name 
)
