ValidationRule.errorConditionFormula:
AND( $Profile.Name <> $Label.Data_Loader_Profile_Name, OR($RecordType.DeveloperName = 'Pre_Sales_Risk_Opportunity', $RecordType.DeveloperName='Pre_Sales_Risk_Opportunity_read_only' ),
OR( AND( OR( ISPICKVAL(StageName , 'Preparation'), ISPICKVAL(StageName , 'In Execution'), ISPICKVAL(StageName , 'Completed'), ISPICKVAL(StageName , 'Cancelled') ), ISPICKVAL(PRIORVALUE(StageName),'Submitted For Approval'), !OR(
NF_PRE_Tech_Field__c='Gate Approved', NF_PRE_Tech_Field__c='Gate Rejected'
) ), AND( OR( ISPICKVAL(StageName ,'Submitted For Approval'), ISPICKVAL(StageName ,'Preparation')
), ISPICKVAL(PRIORVALUE(StageName),'In Execution'), NOT(ISBLANK(NF_PRE_Tech_Field__c)) ) ) )
