ValidationRule.errorConditionFormula:
AND(
$Profile.Name <> $Label.Data_Loader_Profile_Name ,
OR($RecordType.DeveloperName = 'Pre_Sales_Risk_Opportunity_read_only' , $RecordType.DeveloperName = 'Pre_Sales_Risk_Opportunity' ), OR( ISPICKVAL(StageName , 'Submitted For Approval'), ISPICKVAL(StageName , 'In Execution')
), ISPICKVAL(PRIORVALUE(StageName),'Preparation'), (NF_PRE_Tech_Field__c<>'Gate Submitted')
)
